import "./ExpenseItem.css";
function ExpenseItem(props) {
  // return <h1>Expense Item!!</h1>;
  const month = props.toLoacleString("en-US", { month: "long" });
  const date = props.toLoacleString("en-US", { date: "2-digit" });

  const year = props.toLoacleString("en-US", { year: "long" });

  return (
    <div className="expense-item">
      <div>
        <div>{month}</div>
        <div>year</div>
        <div>{date}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
    </div>
  );
}
export default ExpenseItem;
